SQL.statement:
CREATE PROCEDURE insertdata(IN size INT)
LANGUAGE SQL
BEGIN
  DECLARE count INTEGER DEFAULT 0;
  DECLARE commit_flag INTEGER DEFAULT 0;
while (count < size)
  do
     INSERT INTO T1 VALUES(count, '');
     SET count=count+1;
     SET commit_flag=commit_flag+1;
  if (commit_flag=100)
  then commit;
  SET commit_flag=0;
  end if;
  end while;
END
